importgroovy.json.JsonOutput

standardTemplateName = basic.getExtensionPropertyValue(focus, "standardTemplateName")

jsonMap = new HashMap()
jsonMap.add("httpHeaderName1", "X-Scope-OrgID")

if(!basic.isEmpty(standardTemplateName)){
jsonMap.add("standardTemplateName", standardTemplateName)
}

return JsonOutput.toJson(jsonMap)

